import { Component, Input, OnInit } from '@angular/core';
import { IRate } from 'src/app/model/IRate';
import { AppConfigService } from 'src/app/services/app-config.service';
import { DashboardService } from 'src/app/services/dashboard.service';

@Component({
  selector: 'rate-element',
  template: `
    <div
      class="columns-3 gap-1"
      style="display: flex; justify-content: space-between"
    >
      <div style="">
        <img
          style="width: 40px"
          *ngIf="item.name === 'P. Argentino'"
          src="data:image/svg+xml;base64,"
        />

        <img
          style="width: 40px"
          *ngIf="item.name === 'Euro'"
          src="data:image/svg+xml;base64,"
        />

        <img
          style="width: 40px"
          *ngIf="item.name === 'Dólar'"
          src="data:image/svg+xml;base64,"
        />

        <img
          style="width: 40px"
          *ngIf="item.name === 'Real'"
          src="data:image/svg+xml;base64,"
        />
      </div>

      <div
        style="
            width: 80px;
            display: flex;
            align-items: center;
            flex-direction: row;
            align-content: center;
            justify-content: space-between;
          "
      >
        <p
          class="font-bold font-inter"
          style="font-size: 20px"
          [style.color]="appConfigService.theme.primaryColor"
        >
          {{ item.buy | number : '1.2-2' }}
        </p>

        <svg
          *ngIf="item.buy_variation === 'down'"
          style="width: 20px; margin-left: 4px"
          width="44"
          height="22"
          viewBox="0 0 44 22"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path d="M0 0L22 22L44 0H0Z" fill="#ED2A43" />
        </svg>

        <svg
          *ngIf="item.buy_variation === 'up'"
          style="width: 20px; margin-left: 4px"
          width="44"
          height="22"
          viewBox="0 0 44 22"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path d="M0 22L22 0L44 22H0Z" fill="#A0C51E" />
        </svg>

        <svg
          *ngIf="item.buy_variation === 'equal'"
          style="width: 20px; margin-left: 4px"
          width="30"
          height="23"
          viewBox="0 0 30 23"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M0 8.01276V0H30V8.01276H0ZM0 23V14.9872H30V23H0Z"
            fill="#003399"
          />
        </svg>
      </div>

      <div
        style="
            width: 80px;
            display: flex;
            align-items: center;
            flex-direction: row;
            align-content: center;
            justify-content: space-between;
            margin-left: 10px;
          "
      >
        <p
          style="font-size: 20px"
          class="font-bold font-inter"
          [style.color]="appConfigService.theme.primaryColor"
        >
          {{ item.sell | number : '1.2-2' }}
        </p>

        <svg
          *ngIf="item.sell_variation === 'down'"
          style="width: 20px; margin-left: 4px"
          width="44"
          height="22"
          viewBox="0 0 44 22"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path d="M0 0L22 22L44 0H0Z" fill="#ED2A43" />
        </svg>

        <svg
          *ngIf="item.sell_variation === 'up'"
          style="width: 20px; margin-left: 4px"
          width="44"
          height="22"
          viewBox="0 0 44 22"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path d="M0 22L22 0L44 22H0Z" fill="#A0C51E" />
        </svg>

        <svg
          *ngIf="item.sell_variation === 'equal'"
          style="width: 20px; margin-left: 4px"
          width="30"
          height="23"
          viewBox="0 0 30 23"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M0 8.01276V0H30V8.01276H0ZM0 23V14.9872H30V23H0Z"
            fill="#003399"
          />
        </svg>
      </div>
    </div>
   
  `,
  styleUrls: ['./rates-item.component.scss'],
})
export class RatesComponent implements OnInit {
  constructor(public appConfigService: AppConfigService) {}
  @Input() item!: IRate | any;
  ngOnInit() {}
  getFlag(name: string): any {
    switch (name) {
      case 'Dólar':
        return './assets/usa.png';
      case 'Euro':
        return './assets/europe.png';
      case 'P. Argentino':
        return './assets/arg.png';
      case 'Real':
        return './assets/brasil.png';
    }
    return './assets/usa.png';
  }
}
